import React, { useState, useEffect } from "react";
import {
  getGoals,
  addGoal,
  updateGoal,
  deleteGoal,
} from "./api";
import GoalList from "./components/GoalList";
import GoalForm from "./components/GoalForm";
import DepositForm from "./components/DepositForm";
import Overview from "./components/Overview";
import "./styles.css";

export default function App() {
  const [goals, setGoals] = useState([]);

  useEffect(() => {
    getGoals().then(setGoals);
  }, []);

  const handleAddGoal = (goal) => {
    addGoal(goal).then((newGoal) => setGoals([...goals, newGoal]));
  };

  const handleUpdateGoal = (id, updates) => {
    updateGoal(id, updates).then((updatedGoal) => {
      setGoals(goals.map((g) => (g.id === id ? updatedGoal : g)));
    });
  };

  const handleDeleteGoal = (id) => {
    deleteGoal(id).then(() => {
      setGoals(goals.filter((g) => g.id !== id));
    });
  };

  return (
    <div className="container mx-auto px-4 py-6">
      <h1 className="text-3xl font-bold mb-6 text-center">
        üè¶ Smart Goal Planner
      </h1>
      <Overview goals={goals} />
      <GoalForm onAddGoal={handleAddGoal} />
      <DepositForm goals={goals} onDeposit={(id, amount) => {
        const goal = goals.find(g => g.id === id);
        const updatedAmount = goal.savedAmount + amount;
        handleUpdateGoal(id, { savedAmount: updatedAmount });
      }} />
      <GoalList
        goals={goals}
        onUpdateGoal={handleUpdateGoal}
        onDeleteGoal={handleDeleteGoal}
      />
    </div>
  );
}
