import React from "react";
import { daysLeft, isOverdue, isWarning } from "../utils";

const GoalCard = ({ goal, onDeleteGoal }) => {
  const progress = Math.min(
    (goal.savedAmount / goal.targetAmount) * 100,
    100
  ).toFixed(1);

  return (
    <div className="border rounded-lg p-4 shadow-sm bg-white relative">
      <h2 className="text-xl font-semibold mb-2">{goal.name}</h2>
      <p>Category: {goal.category}</p>
      <p>Target: ${goal.targetAmount}</p>
      <p>Saved: ${goal.savedAmount}</p>
      <p>Deadline: {goal.deadline}</p>
      <p>Days Left: {daysLeft(goal.deadline)}</p>

      {isWarning(goal.deadline, goal.savedAmount, goal.targetAmount) && (
        <p className="text-yellow-600 mt-2">⚠️ Less than 30 days left!</p>
      )}
      {isOverdue(goal.deadline, goal.savedAmount, goal.targetAmount) && (
        <p className="text-red-600 mt-2">⏰ Goal Overdue!</p>
      )}

      <div className="w-full bg-gray-200 rounded-full h-3 mt-3">
        <div
          className="bg-green-500 h-3 rounded-full"
          style={{ width: `${progress}%` }}
        ></div>
      </div>
      <p className="text-sm mt-1">{progress}% complete</p>

      <button
        onClick={() => onDeleteGoal(goal.id)}
        className="mt-3 bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
      >
        Delete
      </button>
    </div>
  );
};

export default GoalCard;
