export function daysLeft(deadline) {
  const now = new Date();
  const end = new Date(deadline);
  const diffTime = end - now;
  return Math.ceil(diffTime / (1000 * 60 * 60 * 24)); // Days
}

export function isOverdue(deadline, savedAmount, targetAmount) {
  return daysLeft(deadline) < 0 && savedAmount < targetAmount;
}

export function isWarning(deadline, savedAmount, targetAmount) {
  const days = daysLeft(deadline);
  return days > 0 && days <= 30 && savedAmount < targetAmount;
}
